import { View, Text, ScrollView } from "react-native";
import React, { useEffect, useState } from "react";
import { StyleSheet } from "react-native";
import {
  Button,
  Card,
  Checkbox,
  Paragraph,
  TextInput,
  Title,
} from "react-native-paper";
//import Appbar from 'react-native-paper/lib/typescript/src/components/Appbar/Appbar'
import Appbartab from "../components/Appbartab";
import { Dimensions } from "react-native";
import { CheckBox } from "@rneui/themed";
const { width, height } = Dimensions.get("screen");
const Taskmanager = ({ navigation }) => {
  const [check1, setChecked1] = React.useState(false);
  const [check2, setChecked2] = React.useState(false);
  const [check3, setChecked3] = React.useState(false);
  const [check4, setChecked4] = React.useState(false);
  const [check5, setChecked5] = React.useState(false);

  useEffect(() => {}, []);

  const right = () => <Text>wert-</Text>;

  const arr = [
    ["t1w", "soqwertyu", "false"],
    ["t2", "some", "true"],
    ["t1", "so", "false"],
    ["t1", "so", "true"],
    ["t1", "so", "false"],
    ["t1", "so", "true"],
  ];

  const onpresshandle = (id) => {
    if (id[2] == "true") id[2] = false;
    else id[2] = true;
  };

  return (
    <View style={{ flex: 1 }}>
      <Appbartab />
      <View>
        <Text style={styles.title}>
          {"\n"}Daily Goals{"\n"}
        </Text>
      </View>
      <ScrollView>
        <View style={{ alignItems: "center" }}>
          {arr.map((task) => (
            <Card
              style={{
                margin: 10,
                //marginLeft: 25,
                //marginRight: 25,
                backgroundColor: "#dbe4c6",
                width: width * 0.8,
                //height:height*.1
              }}
            >
              <Card.Title
                title={task[0]}
                right={() => (
                  <CheckBox
                    iconRight
                    containerStyle={{
                      backgroundColor: "#dbe4c6",
                      borderWidth: 0,
                      //borderColor:'black',
                      backfaceVisibility: "hidden",
                      padding: 8,
                      borderRadius: 0,
                    }}
                    center
                    // title=""
                    checked={task[2]}
                    textStyle={{ fontSize: 15 }}
                    onPress={onpresshandle(task[0])}
                    // iconType="material-community"
                    //checkedIcon="clear"
                    //uncheckedIcon="clear"
                    //checkedColor="blue"
                    //size={1}
                  />
                )}
              />
              <Card.Content>
                <Paragraph>{task[1]}</Paragraph>
              </Card.Content>
            </Card>
          ))}
          <Text>{"\n"}</Text>
        </View>
      </ScrollView>
    </View>
  );
};

export default Taskmanager;

const styles = StyleSheet.create({
  container: {
    justifyContent: "center",
    alignItems: "center",
    padding: 20,
    paddingTop: 50,
  },
  title: {
    textAlign: "center",
    fontSize: 20,
    fontWeight: "bold",
  },
  pf: {
    flex: 1,
    flexDirection: "row",
    flexWrap: "wrap",
    alignItems: "flex-start",
  },
  card: {
    justifyContent: "center",
    alignItems: "center",
    padding: 20,
    paddingTop: 40,

    width: 300,
    textAlign: "left",
    fontSize: 20,
    fontWeight: "bold",
  },
  logo: {
    height: 200,
    width: 400,
    resizeMode: "cover",
    marginBottom: 20,
  },
  text: {
    fontFamily: "",
    fontSize: 20,
    marginBottom: 8,
    textAlign: "center",
    color: "#051d5f",
  },
  navButton: {
    marginTop: 15,
  },
  forgotButton: {
    marginVertical: 35,
  },
  datePickerStyle: {
    width: 230,
  },
  navButtonText: {
    fontSize: 18,
    fontWeight: "500",
    color: "#2e64e5",
    fontFamily: "Lato-Regular",
  },
});
