import { StyleSheet, Text, View } from "react-native";
import React, { useState } from "react";
import Appbartab from "../components/Appbartab";
import YoutubePlayer from "react-native-youtube-iframe";
import { Button } from "react-native-paper";
import Bottombar from "../components/Bottombar";
import { Dimensions } from "react-native";

const {width, height} =Dimensions.get('screen');

const Videoscrn = () => {
  const [playing, setPlaying] = useState();

  const togglePlaying = () => {
    setPlaying((prev) => !prev);
  };

  const onStateChange = (state) => {
    if (state === 'ended') {
        setPlaying(false);
        alert('video has finished playing!');
    }
};
  
  return (
    <View>
      <Appbartab />
      <View style={{ margin: 20 }}>
        <Text>for meditation</Text>
        <YoutubePlayer height={200} play={playing} videoId={"wruCWicGBA4"} onChangeState={onStateChange} />
        {/* <Button mode="contained" title={playing ? "pause" : "play"} onPress={togglePlaying} >Play</Button> */}
      </View>

      <View style={{ margin: 20 }}>
        <Text>for meditation</Text>
        <YoutubePlayer height={200} play={playing} videoId={"wruCWicGBA4"} onChangeState={onStateChange} />
        {/* <Button mode="contained" title={playing ? "pause" : "play"} onPress={togglePlaying} >Play</Button> */}
      </View>
        <View style={{marginTop:height*.21}}>
      <Bottombar />
      </View>
    </View>
  );
};

export default Videoscrn;

const styles = StyleSheet.create({});
